import React, { useState, useEffect } from "react";
import Fullcalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

export default function Calendar() {
  const [events, setEvents] = useState([]);
  const [locations, setLocations] = useState([]);
  const [teamMembers, setTeamMembers] = useState([]);
  const [services, setServices] = useState([]);

  // Selected values
  const [selectedLocation, setSelectedLocation] = useState("");
  const [selectedTeamMember, setSelectedTeamMember] = useState("");
  const [selectedService, setSelectedService] = useState("");

  // Load backend data
  useEffect(() => {
    async function loadData() {
      try {
        const [locRes, teamRes, serviceRes, bookingRes] = await Promise.all([
          fetch("http://localhost:4000/api/locations").then((r) => r.json()),
          fetch("http://localhost:4000/api/team-members").then((r) => r.json()),
          fetch("http://localhost:4000/api/services").then((r) => r.json()),
          fetch("http://localhost:4000/api/bookings").then((r) => r.json()),
        ]);

        setLocations(locRes);
        setTeamMembers(teamRes);
        setServices(serviceRes);

        // Map bookings to calendar events
        if (bookingRes.bookings) {
          setEvents(
            bookingRes.bookings.map((b) => ({
              id: b.id,
              title: b.customer?.display_name || "Booking",
              start: b.start_at,
              end: b.end_at,
            }))
          );
        }
      } catch (err) {
        console.error("Failed to load data:", err);
        alert("Error loading data — check console");
      }
    }
    loadData();
  }, []);

  // Create booking
  const handleDateSelect = async (selectInfo) => {
    const customerId = prompt("Enter Customer ID:");
    if (!customerId || !selectedLocation || !selectedTeamMember || !selectedService)
      return alert("Please select location, staff, and service first!");

    const payload = {
      booking: {
        customer_id: customerId,
        location_id: selectedLocation,
        start_at: selectInfo.startStr,
        appointment_segments: [
          {
            team_member_id: selectedTeamMember,
            service_variation_id: selectedService,
            service_variation_version: 1, // Replace with correct version if needed
          },
        ],
      },
    };

    try {
      const res = await fetch("http://localhost:4000/api/bookings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      const data = await res.json();
      if (data.booking) {
        setEvents((prev) => [
          ...prev,
          {
            id: data.booking.id,
            title: data.booking.customer?.display_name || "Booking",
            start: data.booking.start_at,
            end: data.booking.end_at,
          },
        ]);
        alert("Booking created successfully ✅");
      } else {
        console.error(data);
        alert("Failed to create booking — check console");
      }
    } catch (err) {
      console.error(err);
      alert("Error creating booking — check console");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <div style={{ marginBottom: "10px" }}>
        <label>
          Location:
          <select
            value={selectedLocation}
            onChange={(e) => setSelectedLocation(e.target.value)}
          >
            <option value="">Select Location</option>
            {locations.map((loc) => (
              <option key={loc.id} value={loc.id}>
                {loc.name}
              </option>
            ))}
          </select>
        </label>
        <label>
          Staff:
          <select
            value={selectedTeamMember}
            onChange={(e) => setSelectedTeamMember(e.target.value)}
          >
            <option value="">Select Staff</option>
            {teamMembers.map((tm) => (
              <option key={tm.id} value={tm.id}>
                {tm.team_member_details?.given_name || tm.id}
              </option>
            ))}
          </select>
        </label>
        <label>
          Service:
          <select
            value={selectedService}
            onChange={(e) => setSelectedService(e.target.value)}
          >
            <option value="">Select Service</option>
            {services.map((s) => (
              <option key={s.id} value={s.id}>
                {s.item_data?.name || s.id}
              </option>
            ))}
          </select>
        </label>
      </div>

      <Fullcalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="timeGridWeek"
        selectable={true}
        events={events}
        select={handleDateSelect}
      />
    </div>
  );
}
