import React, { useState, useEffect } from 'react';
import Fullcalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

export default function Calendar() {
  const [events, setEvents] = useState([]);

  // Fetch bookings from backend
  useEffect(() => {
    fetch("http://localhost:4000/api/bookings")
      .then(res => res.json())
      .then(data => {
        if (data.bookings) {
          const mapped = data.bookings.map(b => ({
            id: b.id,
            title: b.customer?.display_name || "Booking",
            start: b.start_at,
            end: b.end_at,
          }));
          setEvents(mapped);
        }
      });
  }, []);

  // Handle slot selection → create booking
  const handleDateSelect = (selectInfo) => {
  const customerId = prompt("Enter Customer ID:");
  if (!customerId) return;

  fetch("http://localhost:4000/api/bookings", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      booking: {
        customer_id: customerId,
        location_id: "YOUR_LOCATION_ID",
        start_at: selectInfo.startStr,
        appointment_segments: [
          {
            team_member_id: "YOUR_TEAM_MEMBER_ID",
            service_variation_id: "YOUR_SERVICE_VARIATION_ID",
            service_variation_version: YOUR_SERVICE_VARIATION_VERSION
          }
        ]
      }
    }),
  })
  .then(res => res.json())
  .then(data => {
    console.log("Created booking response:", data);
    if (data.booking) {
      setEvents(prev => [
        ...prev,
        {
          id: data.booking.id,
          title: data.booking.customer?.display_name || "Booking",
          start: data.booking.start_at,
          end: data.booking.end_at,
        },
      ]);
    } else {
      alert("Booking failed: " + (data.errors?.[0]?.detail || "Unknown error"));
    }
  })
  .catch(err => {
    console.error("Create booking error:", err);
    alert("Error creating booking — check console.");
  });
};

  return (
    <div style={{ padding: "20px" }}>
      <Fullcalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="timeGridWeek"
        selectable={true}
        events={events}
        select={handleDateSelect}
      />
    </div>
  );
}
