import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

export default function Calendar() {
  const [events, setEvents] = useState([]);
  const [locations, setLocations] = useState([]);
  const [teamMembers, setTeamMembers] = useState([]);
  const [services, setServices] = useState([]);

  const [showBookingForm, setShowBookingForm] = useState(false);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [customerId, setCustomerId] = useState("");
  const [selectedLocation, setSelectedLocation] = useState("");
  const [selectedTeamMember, setSelectedTeamMember] = useState("");
  const [selectedService, setSelectedService] = useState("");

  const [sidebarDay, setSidebarDay] = useState(new Date());
  const [availableSlots, setAvailableSlots] = useState([]);

  // Load backend data
  useEffect(() => {
    async function loadData() {
      try {
        const [locRes, teamRes, serviceRes, bookingRes] = await Promise.all([
          fetch("http://localhost:4000/api/locations").then((r) => r.json()),
          fetch("http://localhost:4000/api/team-members").then((r) => r.json()),
          fetch("http://localhost:4000/api/services").then((r) => r.json()),
          fetch("http://localhost:4000/api/bookings").then((r) => r.json()),
        ]);

        setLocations(Array.isArray(locRes) ? locRes : []);
        setTeamMembers(
          teamRes?.team_members || [
            {
              id: "test1",
              team_member_details: { given_name: "John", family_name: "Doe" },
            },
            {
              id: "test2",
              team_member_details: { given_name: "Jane", family_name: "Smith" },
            },
          ]
        );
        setServices(
          serviceRes?.objects || [
            { id: "srv1", item_data: { name: "Haircut" } },
            { id: "srv2", item_data: { name: "Massage" } },
          ]
        );
        setEvents(
          Array.isArray(bookingRes)
            ? bookingRes.map((b) => ({
                id: b.id,
                title: b.customer?.display_name || "Booking",
                start: new Date(b.start_at),
                end: new Date(b.end_at),
                team_member_id: b.team_member_id,
              }))
            : []
        );
      } catch (err) {
        console.error("Failed to load data:", err);
        alert("Error loading data — check console");
      }
    }
    loadData();
  }, []);

  // Compute available slots for sidebar
  useEffect(() => {
    if (!selectedTeamMember) {
      setAvailableSlots([]);
      return;
    }

    const slots = [];
    const startHour = 9;
    const endHour = 17;

    for (let hour = startHour; hour < endHour; hour++) {
      const start = new Date(sidebarDay);
      start.setHours(hour, 0, 0, 0);
      const end = new Date(sidebarDay);
      end.setHours(hour + 1, 0, 0, 0);

      const conflict = events.some(
        (e) =>
          e.team_member_id === selectedTeamMember &&
          start < e.end &&
          end > e.start
      );

      if (!conflict) slots.push({ start, end });
    }
    setAvailableSlots(slots);
  }, [events, sidebarDay, selectedTeamMember]);

  const handleDateSelect = (selectInfo) => {
    const start = new Date(selectInfo.start);
    const end = new Date(selectInfo.end);

    const conflict = events.some(
      (e) =>
        e.team_member_id === selectedTeamMember &&
        start < e.end &&
        end > e.start
    );

    if (conflict) {
      alert("This slot is already booked!");
      return;
    }

    setSelectedSlot({ start, end });
    setShowBookingForm(true);
  };

  const createBooking = async () => {
    if (
      !customerId ||
      !selectedLocation ||
      !selectedTeamMember ||
      !selectedService
    ) {
      return alert("Please fill all fields!");
    }

    // Prevent double booking on frontend
    const conflict = events.some(
      (e) =>
        e.team_member_id === selectedTeamMember &&
        new Date(selectedSlot.start) < e.end &&
        new Date(selectedSlot.end) > e.start
    );
    if (conflict) return alert("Selected slot is already booked!");

    const payload = {
      booking: {
        customer_id: customerId,
        location_id: selectedLocation,
        start_at: selectedSlot.start.toISOString(),
        end_at: selectedSlot.end.toISOString(),
        appointment_segments: [
          {
            team_member_id: selectedTeamMember,
            service_variation_id: selectedService,
            service_variation_version: 1,
          },
        ],
      },
    };

    try {
      const res = await fetch("http://localhost:4000/api/bookings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      const data = await res.json();

      if (data.id) {
        setEvents((prev) => [
          ...prev,
          {
            id: data.id,
            title: data.customer?.display_name || "Booking",
            start: new Date(data.start_at),
            end: new Date(data.end_at),
            team_member_id: selectedTeamMember,
          },
        ]);

        setShowBookingForm(false);
        setCustomerId("");
        setSelectedLocation("");
        setSelectedTeamMember("");
        setSelectedService("");
        setSelectedSlot(null);

        alert("Booking created ✅");
      } else {
        console.error(data);
        alert("Failed to create booking — check console");
      }
    } catch (err) {
      console.error(err);
      alert("Error creating booking — check console");
    }
  };

  return (
    <div style={{ display: "flex", padding: "20px" }}>
      {/* Sidebar */}
      <div style={{ width: "200px", marginRight: "20px" }}>
        <h4>Available Times</h4>
        <label>
          Select Day:{" "}
          <input
            type="date"
            value={sidebarDay.toISOString().split("T")[0]}
            onChange={(e) => setSidebarDay(new Date(e.target.value))}
          />
        </label>
        <label style={{ display: "block", marginTop: "10px" }}>
          Select Staff:{" "}
          <select
            value={selectedTeamMember}
            onChange={(e) => setSelectedTeamMember(e.target.value)}
          >
            <option value="">Select Staff</option>
            {teamMembers.map((tm) => (
              <option key={tm.id} value={tm.id}>
                {tm.team_member_details?.given_name || tm.id}
              </option>
            ))}
          </select>
        </label>
        {availableSlots.length ? (
          availableSlots.map((slot) => (
            <button
              key={slot.start.toISOString()}
              style={{
                display: "block",
                width: "100%",
                marginBottom: "5px",
                padding: "5px",
              }}
              onClick={() => {
                setSelectedSlot({ start: slot.start, end: slot.end });
                setShowBookingForm(true);
              }}
            >
              {slot.start.toLocaleTimeString([], {
                hour: "2-digit",
                minute: "2-digit",
              })}{" "}
              -{" "}
              {slot.end.toLocaleTimeString([], {
                hour: "2-digit",
                minute: "2-digit",
              })}
            </button>
          ))
        ) : (
          <p>No available slots</p>
        )}
      </div>

      {/* Calendar */}
      <div style={{ flex: 1 }}>
        {showBookingForm && (
          <div
            style={{
              border: "1px solid #ccc",
              padding: "10px",
              marginBottom: "20px",
              borderRadius: "6px",
              backgroundColor: "#f9f9f9",
            }}
          >
            <h3>Create Booking</h3>
            <label>
              Customer ID:
              <input
                type="text"
                value={customerId}
                onChange={(e) => setCustomerId(e.target.value)}
              />
            </label>
            <label>
              Location:
              <select
                value={selectedLocation}
                onChange={(e) => setSelectedLocation(e.target.value)}
              >
                <option value="">Select Location</option>
                {locations.map((loc) => (
                  <option key={loc.id} value={loc.id}>
                    {loc.name}
                  </option>
                ))}
              </select>
            </label>
            <label>
              Staff:
              <select
                value={selectedTeamMember}
                onChange={(e) => setSelectedTeamMember(e.target.value)}
              >
                <option value="">Select Staff</option>
                {teamMembers.map((tm) => (
                  <option key={tm.id} value={tm.id}>
                    {tm.team_member_details?.given_name || tm.id}
                  </option>
                ))}
              </select>
            </label>
            <label>
              Service:
              <select
                value={selectedService}
                onChange={(e) => setSelectedService(e.target.value)}
              >
                <option value="">Select Service</option>
                {services.map((s) => (
                  <option key={s.id} value={s.id}>
                    {s.item_data?.name || s.id}
                  </option>
                ))}
              </select>
            </label>
            <button onClick={createBooking} style={{ marginRight: "10px" }}>
              Create Booking
            </button>
            <button onClick={() => setShowBookingForm(false)}>Cancel</button>
          </div>
        )}

        <FullCalendar
          timeZone="local"
          plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
          initialView="timeGridWeek"
          selectable={true}
          events={events}
          select={handleDateSelect}
        />
      </div>
    </div>
  );
}
