import React, { useState, useEffect } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import interactionPlugin from "@fullcalendar/interaction";
import "./Calendar.css";

export default function Calendar({
  events = [],
  locations = [],
  teamMembers = [],
  services = [],
  selectedLocation,
  setSelectedLocation,
  selectedTeamMember,
  setSelectedTeamMember,
  selectedService,
  setSelectedService,
  setShowBookingForm = () => {},
  setSelectedSlot = () => {},
}) {
  const [sidebarDay, setSidebarDay] = useState(new Date());
  const [slots, setSlots] = useState([]);

  // build slots (mark available/unavailable)
  useEffect(() => {
    if (!selectedTeamMember) {
      setSlots([]);
      return;
    }

    const daySlots = [];
    for (let hour = 9; hour < 17; hour++) {
      const start = new Date(sidebarDay);
      start.setHours(hour, 0, 0, 0);
      const end = new Date(sidebarDay);
      end.setHours(hour + 1, 0, 0, 0);

      const conflict = events.some(
        (e) =>
          e.team_member_id === selectedTeamMember &&
          new Date(e.start) < end &&
          new Date(e.end) > start
      );

      daySlots.push({ start, end, conflict });
    }
    setSlots(daySlots);
  }, [events, sidebarDay, selectedTeamMember]);

  return (
    <div className="calendar-container">
      <div className="calendar-sidebar">
        <h3>📅 Select Date & Time</h3>
        <p>Choose your staff, service, location, and a time slot.</p>

        {/* Staff Selector */}
        <div className="form-group">
          <label>Staff:</label>
          <select
            value={selectedTeamMember}
            onChange={(e) => setSelectedTeamMember(e.target.value)}
          >
            <option value="">Select Staff</option>
            {teamMembers.map((tm) => (
              <option key={tm.id} value={tm.id}>
                {tm.team_member_details?.given_name || tm.id}
              </option>
            ))}
          </select>
        </div>

        {/* Location Selector */}
        <div className="form-group">
          <label>Location:</label>
          <select
            value={selectedLocation}
            onChange={(e) => setSelectedLocation(e.target.value)}
          >
            <option value="">Select Location</option>
            {locations.map((loc) => (
              <option key={loc.id} value={loc.id}>
                {loc.name}
              </option>
            ))}
          </select>
        </div>

        {/* Service Selector */}
        <div className="form-group">
          <label>Service:</label>
          <select
            value={selectedService}
            onChange={(e) => setSelectedService(e.target.value)}
          >
            <option value="">Select Service</option>
            {services.map((s) => (
              <option key={s.id} value={s.id}>
                {s.item_data?.name || s.id}
              </option>
            ))}
          </select>
        </div>

        {/* Mini calendar (only one calendar now) */}
        <div className="mini-calendar">
          <FullCalendar
            plugins={[dayGridPlugin, interactionPlugin]}
            initialView="dayGridMonth"
            selectable={true}
            select={(info) => setSidebarDay(new Date(info.startStr))}
            headerToolbar={{
              left: "prev",
              center: "title",
              right: "next",
            }}
            height="auto"
          />
        </div>

        {/* Time slots */}
        <div className="time-slots">
          {!selectedTeamMember ? (
            <p style={{ color: "#888", fontStyle: "italic" }}>
              Please select a staff member to see available slots
            </p>
          ) : (
            slots.map((slot) => (
              <button
                key={slot.start.toISOString()}
                className={`time-slot-btn ${
                  slot.conflict ? "unavailable" : "available"
                }`}
                disabled={slot.conflict}
                onClick={() => {
                  if (!slot.conflict) {
                    setSelectedSlot({
                      start: slot.start.toISOString(),
                      end: slot.end.toISOString(),
                    });
                    setShowBookingForm(true);
                  }
                }}
              >
                {slot.start.toLocaleTimeString([], {
                  hour: "2-digit",
                  minute: "2-digit",
                })}{" "}
                -{" "}
                {slot.end.toLocaleTimeString([], {
                  hour: "2-digit",
                  minute: "2-digit",
                })}
                {slot.conflict ? " ❌" : " ✅"}
              </button>
            ))
          )}
        </div>

        {/* Legend */}
        <div className="legend">
          <span className="dot available"></span> Available
          <span className="dot unavailable"></span> Unavailable
        </div>
      </div>
    </div>
  );
}
