import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Calendar() {
  const [selectedDate, setSelectedDate] = useState(null);
  const [availableSlots, setAvailableSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    service: "",
    notes: "",
  });
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  /** Fetch services on load */
  useEffect(() => {
    axios.get("/api/services").then((res) => {
      setServices(res.data);
    });
  }, []);

  /** Example slots generator (9AM–5PM hourly) */
  const generateSlots = (date) => {
    const slots = [];
    for (let hour = 9; hour <= 17; hour++) {
      const start = new Date(date);
      start.setHours(hour, 0, 0, 0);
      const end = new Date(start);
      end.setHours(start.getHours() + 1);

      slots.push({
        start: start.toISOString(),
        end: end.toISOString(),
        label: `${hour}:00 - ${hour + 1}:00`,
      });
    }
    return slots;
  };

  const handleDateSelect = (date) => {
    setSelectedDate(date);
    setAvailableSlots(generateSlots(date));
    setSelectedSlot(null);
  };

  const handleBooking = async (e) => {
    e.preventDefault();
    if (!selectedSlot || !formData.name || !formData.email || !formData.phone || !formData.service) {
      setMessage("Please complete all required fields.");
      return;
    }

    setLoading(true);
    setMessage("");

    try {
      const booking = {
        start_at: selectedSlot.start,
        end_at: selectedSlot.end,
        appointment_segments: [
          {
            team_member_id: "test1", // mock staff
            service_variation_id: formData.service,
          },
        ],
        customer_id: formData.name,
      };

      const res = await axios.post("/api/bookings", { booking });
      setMessage("✅ Appointment booked successfully!");
      console.log("Booking response:", res.data);
    } catch (err) {
      if (err.response?.status === 409) {
        setMessage("❌ That time slot is already booked. Please select another.");
      } else {
        setMessage("⚠️ Failed to book appointment.");
      }
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col lg:flex-row gap-8 p-8 bg-white rounded-2xl shadow-md">
      {/* Left Side - Calendar */}
      <div className="flex-1">
        <h3 className="text-pink-600 font-semibold mb-2">Select Date & Time</h3>
        <p className="text-gray-600 mb-4">
          Choose your preferred appointment date and available time slot.
        </p>

        {/* Simple Calendar */}
        <div className="border border-gray-200 rounded-xl p-4">
          <input
            type="date"
            className="p-2 border border-gray-300 rounded-lg w-full mb-4"
            onChange={(e) => handleDateSelect(new Date(e.target.value))}
          />

          {/* Time Slots */}
          {selectedDate && (
            <div className="grid grid-cols-2 gap-2">
              {availableSlots.map((slot) => (
                <button
                  key={slot.start}
                  onClick={() => setSelectedSlot(slot)}
                  className={`p-2 rounded-lg text-sm ${
                    selectedSlot?.start === slot.start
                      ? "bg-pink-600 text-white"
                      : "bg-gray-100 hover:bg-gray-200"
                  }`}
                >
                  {slot.label}
                </button>
              ))}
            </div>
          )}

          {/* Legend */}
          <div className="flex gap-6 mt-4">
            <span className="flex items-center gap-1 text-pink-600">
              <span className="w-3 h-3 rounded-full bg-pink-600"></span> Available
            </span>
            <span className="flex items-center gap-1 text-gray-400">
              <span className="w-3 h-3 rounded-full bg-gray-400"></span> Unavailable
            </span>
          </div>
        </div>
      </div>

      {/* Right Side - Form */}
      <div className="flex-1 flex flex-col">
        <h3 className="text-pink-600 font-semibold mb-2">Your Details</h3>
        <p className="text-gray-600 mb-4">
          Fill out your information to complete the booking.
        </p>

        <form className="flex flex-col gap-4" onSubmit={handleBooking}>
          <input
            type="text"
            placeholder="Full Name"
            className="p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-400 outline-none"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            required
          />
          <input
            type="email"
            placeholder="Email"
            className="p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-400 outline-none"
            value={formData.email}
            onChange={(e) => setFormData({ ...formData, email: e.target.value })}
            required
          />
          <input
            type="tel"
            placeholder="Phone Number"
            className="p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-400 outline-none"
            value={formData.phone}
            onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
            required
          />
          <select
            className="p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-400 outline-none"
            value={formData.service}
            onChange={(e) => setFormData({ ...formData, service: e.target.value })}
            required
          >
            <option value="">Select a service</option>
            {services.map((srv) => (
              <option key={srv.id} value={srv.id}>
                {srv.item_data?.name}
              </option>
            ))}
          </select>
          <textarea
            placeholder="Any special requests or questions?"
            className="p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-pink-400 outline-none resize-none min-h-[80px]"
            value={formData.notes}
            onChange={(e) => setFormData({ ...formData, notes: e.target.value })}
          ></textarea>

          <button
            type="submit"
            disabled={loading}
            className="bg-pink-600 text-white font-bold p-3 rounded-xl hover:bg-pink-700 disabled:bg-pink-300"
          >
            {loading ? "Booking..." : "Book Appointment"}
          </button>
        </form>

        {message && <p className="mt-4 text-sm text-center">{message}</p>}
      </div>
    </div>
  );
}
